import React from 'react'
import { Label, Modal, Button, Header } from 'semantic-ui-react'
import PropTypes from 'prop-types'

function ModalPop({content}) {
  return (
    <div>
      <Modal style ={{borderless: 'true', width: '40%', height: '40%'}} trigger = {<Button>Modal Trigger Node</Button>}>
      <Modal.Content style ={{borderless: 'true'}}>
        <Header style = {{fontSize: '2vw', textAlign: 'center'}}>{content}</Header>
        <Button style = {{marginRight: '10%'}} negative>No</Button>
        <Button positive>Yes</Button>
      </Modal.Content>
      </Modal>
    </div>
  )
}

ModalPop.propTypes = {
  content: PropTypes.node,
}

export default ModalPop
