import React, {useState, useEffect} from 'react'
import LargeLabel from '../components/largeLabel'
import DropDown from '../components/dropDown'
import Layout from '../components/layout'
import ModalPop from '../components/modal'
import JoinRequestModal from '../components/joinRequestModal'
import InviteReceiveModal from '../components/inviteReceiveModal'
import StudentInviteModal from '../components/StudentInviteModal'

import * as api from '../util/mercuryService'

const friendOptions = [
  {
    key: 'Jenny Hess',
    text: 'Jenny Hess',
    value: 'Jenny Hess',
    image: {
      avatar: true,
      src: 'https://react.semantic-ui.com/images/avatar/small/jenny.jpg',
    },
  },
  {
    key: 'Elliot Fu',
    text: 'Elliot Fu',
    value: 'Elliot Fu',
    image: {
      avatar: true,
      src: 'https://react.semantic-ui.com/images/avatar/small/elliot.jpg',
    },
  },
  {
    key: 'Stevie Feliciano',
    text: 'Stevie Feliciano',
    value: 'Stevie Feliciano',
    image: {
      avatar: true,
      src: 'https://react.semantic-ui.com/images/avatar/small/stevie.jpg',
    },
  },
]

function Calendar() {
  const [classes, setClasses] = useState([])

      useEffect(() => {
        api
          .getClasses()
          .then((classes) => setClasses(classes))
          .catch(console.error)
      })

  // const calendarIds = [
  //   'avnpisdeelacuaq11un5otu5k8@group.calendar.google.com',
  //   '4ctv2ua6npuegf05gh00iukd5g@group.calendar.google.com',
  // ]

  function mergeCal(classList) {
    var src = 'https://calendar.google.com/calendar/embed?mode=WEEK&showTitle=0'
    classList.forEach((c) => {
      src = src + '&src=' + c.calendarId
    })
    console.log(classList)
    return src
  }

  return (
    <Layout
      left={
        <div style={{ paddingLeft: 20, paddingRight: 20 }}>
          <LargeLabel content={<p>Classes</p>}></LargeLabel>
          <DropDown></DropDown>
          <ModalPop content={<p>Test</p>}></ModalPop>

          <JoinRequestModal
            content={'[student] requested to join your group'}
          ></JoinRequestModal>
          <InviteReceiveModal
            content={'[student] invites you to join [group#]'}
          ></InviteReceiveModal>
          <StudentInviteModal
            buttonText={'Invite'}
            placeholder={'Add student to your group...'}
          ></StudentInviteModal>
        </div>
      }
    >
      <iframe
        src={mergeCal(classes)}
        style={{ border: '0' }}
        width={'100%'}
        height={'100%'}
        frameBorder={0}
        scrolling="no"
      ></iframe>
    </Layout>
  )
}

export default Calendar
